mapper.select:
        SELECT
        

--         级联表的字段

        FROM fuwuxiangmu  a

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.fuwuxiangmu_name like CONCAT('%',#{params.fuwuxiangmuName},'%')
            
            
                and a.fuwuxiangmu_types = #{params.fuwuxiangmuTypes}
            
            
                and a.fuwuxiangmu_content like CONCAT('%',#{params.fuwuxiangmuContent},'%')
            
            
                  and a.fuwuxiangmu_delete >= #{params.fuwuxiangmuDeleteStart}   
            
            
                  and a.fuwuxiangmu_delete <= #{params.fuwuxiangmuDeleteEnd}   
            
             
                and a.fuwuxiangmu_delete = #{params.fuwuxiangmuDelete}
             
            
                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 
            
            
                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 
            

        
        order by a.${params.sort} ${params.order}
