mapper.select:
        SELECT
        

--         级联表的字段
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.yonghu_age as yonghuAge
        ,yonghu.jiankang_types as jiankangTypes
        ,yonghu.yonghu_delete as yonghuDelete

        FROM jiashu  a
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.username like CONCAT('%',#{params.username},'%')
            
            
                and a.password like CONCAT('%',#{params.password},'%')
            
            
                and a.jiashu_name like CONCAT('%',#{params.jiashuName},'%')
            
            
                and a.sex_types = #{params.sexTypes}
            
            
                and a.jiashu_phone like CONCAT('%',#{params.jiashuPhone},'%')
            
            
                and a.jiashu_email like CONCAT('%',#{params.jiashuEmail},'%')
            
            
                and a.shenfen_types = #{params.shenfenTypes}
            
            
                and (
                    a.yonghu_id = #{params.yonghuId}
                )
            
            
                  and a.jiashu_delete >= #{params.jiashuDeleteStart}   
            
            
                  and a.jiashu_delete <= #{params.jiashuDeleteEnd}   
            
             
                and a.jiashu_delete = #{params.jiashuDelete}
             

                
            
                and a.yonghu_id IS NOT NULL
            
            
                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
            
            
                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
            
            
                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
            
            
                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
            
            
                  and yonghu.yonghu_age >= #{params.yonghuAgeStart}   
            
            
                  and yonghu.yonghu_age <= #{params.yonghuAgeEnd}   
            
            
                and yonghu.yonghu_age = #{params.yonghuAge}
            
            
                and yonghu.jiankang_types = #{params.jiankangTypes}
            

            
                  and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   
            
            
                  and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   
            
            
                and yonghu.yonghu_delete = #{params.yonghuDelete}
            
        
        order by a.${params.sort} ${params.order}
