mapper.select:
SELECT
-- 级联表的字段 ,yisheng.yisheng_uuid_number as yishengUuidNumber ,yisheng.yisheng_name as yishengName ,yisheng.yisheng_types as yishengTypes ,yisheng.zhiwei_types as zhiweiTypes ,yisheng.yisheng_zhichneg as yishengZhichneg ,yisheng.yisheng_photo as yishengPhoto ,yisheng.yisheng_phone as yishengPhone ,yisheng.yisheng_guahao as yishengGuahao ,yisheng.yisheng_email as yishengEmail ,yisheng.yisheng_content as yishengContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_age as yonghuAge ,yonghu.jiankang_types as jiankangTypes ,yonghu.yonghu_delete as yonghuDelete
FROM yisheng_yuyue a left JOIN yisheng yisheng ON a.yisheng_id = yisheng.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.yisheng_id = #{params.yishengId} )
and ( a.yonghu_id = #{params.yonghuId} )
and UNIX_TIMESTAMP(a.yisheng_yuyue_time) >= UNIX_TIMESTAMP(#{params.yishengYuyueTimeStart})
and UNIX_TIMESTAMP(a.yisheng_yuyue_time) <= UNIX_TIMESTAMP(#{params.yishengYuyueTimeEnd})
and a.yisheng_yuyue_text like CONCAT('%',#{params.yishengYuyueText},'%')
and a.yisheng_yuyue_yesno_types = #{params.yishengYuyueYesnoTypes}
and a.yisheng_yuyue_yesno_text like CONCAT('%',#{params.yishengYuyueYesnoText},'%')
and a.yisheng_id IS NOT NULL
and yisheng.yisheng_uuid_number like CONCAT('%',#{params.yishengUuidNumber},'%')
and yisheng.yisheng_name like CONCAT('%',#{params.yishengName},'%')
and yisheng.yisheng_types = #{params.yishengTypes}
and yisheng.zhiwei_types = #{params.zhiweiTypes}
and yisheng.yisheng_zhichneg like CONCAT('%',#{params.yishengZhichneg},'%')
and yisheng.yisheng_phone like CONCAT('%',#{params.yishengPhone},'%')
and yisheng.yisheng_guahao like CONCAT('%',#{params.yishengGuahao},'%')
and yisheng.yisheng_email like CONCAT('%',#{params.yishengEmail},'%')
and yisheng.yisheng_content like CONCAT('%',#{params.yishengContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_age >= #{params.yonghuAgeStart}
and yonghu.yonghu_age <= #{params.yonghuAgeEnd}
and yonghu.yonghu_age = #{params.yonghuAge}
and yonghu.jiankang_types = #{params.jiankangTypes}
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.sort} ${params.order}
